mapper.select:
	 
select no, name, email, gender
  from user 
 where no = #{value }

	 
select no, name, email, gender
  from user 
 where email = #{value }

	 
select no, name
  from user 
 where email=#{email } 
   and password=#{password }

mapper.update:
update user set name = #{name },
password=#{password },
gender = #{gender }
where no = #{no }
